function field($field_name,$is_required,$field_type,$max_length,$description,$style)
{
   global $output;
   $output='';
   global $field_list;
   $field_list.="$field_name,$is_required,$field_type,$description\n";

   //if ($field_type=='date') $field_type='text';
   // make non-required fields collapsed
   // add descriptions, choices, yesno, zip, phone style inputs
   
   ////////////////////////////////////////////////////////////////////////
   // philvaz: check for $style and display it inside proper html form tag
   ////////////////////////////////////////////////////////////////////////
                  
   $value='';
   @$value=$_SESSION[$field_name];

   if($field_type != 'hidden') // TYPE IS NOT HIDDEN
   {
      if ($is_required)
      {
         if ( ($style=='') or (strpos($style,'class')=== 0)) xecho("<span style=\"color: red; visibility: invisible;\">*</span>");
      }
   }
 
   if($field_type == 'textarea') // TYPE IS TEXTAREA
   {
      xecho("<textarea name='$field_name' id='$field_name'");
      if($is_required)
      {
         echo(" required='required'");
      }
      xecho(">$value</textarea><br>");
   }
   
   elseif($field_type=='state') // TYPE IS STATE DROP DOWN LIST
   {
      xecho("<select name='$field_name' id='$field_name'>");
      xecho(states($value));
      xecho("</select>");
   }

   elseif(strpos($field_type,'|') !== false) // TYPE IS SELECT DROP DOWN LIST
   {
      xecho("$description <select name='$field_name' id='$field_name'>");
      $oarr=explode('|',$field_type);
      xecho("<option selected disabled=\"disabled\">Select</option>");
 
      foreach($oarr as $option)
      {
         xecho("<option value='$option'>$option</option>");
      }

      xecho("</select>");
   }

   elseif($field_type == 'yesno') // TYPE IS SIMPLE YES/NO DROP DOWN
   {
      xecho(" $description <select name='$field_name' id='$field_name'>");
      xecho("<option value='1'>Yes</option>");
      xecho("<option value='zero!'>No</option>"); // stupid form will not post a 0. proxy fixes it
      xecho("</select>");
   }

   elseif($field_type == 'noyes') // TYPE IS SIMPLE NO/YES DROP DOWN
   {
      xecho(" $description <select name='$field_name' id='$field_name'>");
      xecho("<option value='zero!'>No</option>"); // stupid form will not post a 0. proxy fixes it
      xecho("<option value='1'>Yes</option>");
      xecho("</select>");
   }

   elseif($field_type == 'hidden') // TYPE IS HIDDEN
   {
      xecho("<input type='hidden' name='$field_name' id='$field_name' value='$value'>");
   }

   elseif($field_type == 'date') // TYPE IS DATE -- NEED DATE PICKER
   {
      $txt="$description <input type='text'";
      if($is_required)
      {
         $txt.=" required='required'";
      }

      $txt.=" class='date' name='$field_name' id='$field_name' value='$value' $style>";
      xecho($txt);
   }

   else  // TYPE IS TEXT BOX
   {
      // format is e.g. <input type='text' placeholder='First Name' required='required' style='width: 187px;'>

      if (strpos($description,':') > 0)
      {
         xecho("<div style='clear:both;'></div><div style='float:left;position:relative; top:7px;'>");
         xecho(substr($description,0,strpos($description,':')));
         xecho("</div>");

         $placeholder=substr($description,strpos($description,':')+1);
         xecho("<div style='float:right;'>");
      }
      else
      {
         $placeholder=$description;
         xecho("<label for='$field_name'>$placeholder</label>");
         $placeholder='';
      }

      xecho("<input type='$field_type' placeholder='$placeholder'");

      if($is_required)
      {
         xecho(" required='required'");
      }

      xecho(" name='$field_name' id='$field_name' value='$value' $style>");

      if (strpos($style,'class') != false) $style='';

      if (strpos($description,':') > 0)
      {
         xecho("</div>");
      }
      else
      {
         xecho("</label>");
         $style='';
      }

   } // end long else

   if (($field_type=='text') and ($style==''))
   {
      $output="<div class='slider'>$output</div>";
   }

   return($output);

} // end function field


function xecho($txt)
{
	global $output; 
	$output.="$txt\n";
}

